mapper.insert:
/* insertErpDailyServiceMain */

INSERT INTO ERP_DAILY_SERVICE (

        ERP_SERVICE_ID
       ,ERP_MAIN_ID
       ,ERP_MAIL_CHK
       ,ERP_MAIL_BIGO
       ,ERP_APPROVE_CHK
       ,ERP_APPROVE_BIGO
       ,ERP_BACKUP_CHK
       ,ERP_BACKUP_BIGO
) VALUES (

        #{erpServiceId}
       ,#{erpMainId}
       ,#{erpMailServiceChk}
       ,#{erpMailServiceBigo}
       ,#{erpApproveServiceChk}
       ,#{erpApproveServiceBigo}
       ,#{erpBackUpServiceChk}
       ,#{erpBackUpServiceBigo}
)

/* insertErpDailyStorageMain */

INSERT INTO ERP_DAILY_STORAGE (

