mapper.select:
/* selectErpDailyServiceMain */
SELECT ERP_SERVICE_ID AS erpServiceId ,ERP_MAIN_ID AS erpMainId ,ERP_MAIL_CHK AS erpMailServiceChk ,ERP_MAIL_BIGO AS erpMainServiceBigo ,ERP_APPROVE_CHK AS erpApproveServiceChk ,ERP_APPROVE_BIGO AS erpApproveServiceBigo ,ERP_BACKUP_CHK AS erpBackUpServiceChk ,ERP_BACKUP_BIGO AS erpBackUpServiceBigo FROM ERP_DAILY_SERVICE
WHERE ERP_MAIN_ID = #{erpMainId}

