INSERT INTO GW_DAILY_STORAGE (

     GW_STORAGE_ID
    ,GW_MAIN_ID
    ,GW_NASMAIN_CHK
    ,GW_NASMAIN_BIGO
    ,GW_NASBACKUP_CHK
    ,GW_NASBACKUP_BIGO
    ,ARCH_NASMAIN_CHK
    ,ARCH_NASMAIN_BIGO
    ,ARCH_NASBACKUP_CHK
    ,ARCH_NASBACKUP_BIGO

) VALUES (

     1
    ,2
    ,#{gwNasMainClustChk}
    ,#{gwNasMainClustBigo}
    ,#{gwNasBackClustChk}
    ,#{gwNasBackClustBigo}
    ,#{archNasMainClustChk}
    ,#{archNasMainClustBigo}
    ,#{archNasBackClustChk}
    ,#{archNasBackClustBigo}
)

mapper.select:
SELECT
GW_STORAGE_ID AS gwStorageId ,GW_MAIN_ID AS gwMainId ,GW_NASMAIN_CHK AS gwNasMainClustChk ,GW_NASMAIN_BIGO AS gwNasMainClustBigo ,GW_NASBACKUP_CHK AS gwNasBackClustChk ,GW_NASBACKUP_BIGO AS gwNasBackClustBigo ,ARCH_NASMAIN_CHK AS gwNasMainClustChk ,ARCH_NASMAIN_BIGO AS gwNasMainCLustBigo ,ARCH_NASBACKUP_CHK AS gwNasBackClustChk ,ARCH_NASBACKUP_BIGO AS gwNasBackClustBigo
FROM GW_DAILY_STORAGE WHERE GW_MAIN_ID= #{gwMainId}
