/* selectInfraDailyServiceMain */
SELECT INFRA_SERVICE_ID AS infraServiceId ,INFRA_MAIN_ID AS infraMainId ,INFRA_BACKUP_CHK AS infraBackUpServiceChk ,INFRA_BACKUP_BIGO AS infraBackUpServiceBigo ,INFRA_SPAM_CHK AS infraSpamServiceChk ,INFRA_SPAM_BIGO AS infraSpamServiceBigo ,INFRA_MAIL_CHK AS infraMailServiceChk ,INFRA_MAIL_BIGO AS infraMailServiceBigo ,INFRA_DNS_CHK AS infraDNSServiceChk ,INFRA_DNS_BIGO AS infraDNSServiceBigo FROM
INFRA_DAILY_SERVICE
WHERE
INFRA_MAIN_ID = #{infraMainId}
